() => {
  if (Locations.selectedOptionValue == '') {
    showAlert("Vui lòng thêm vào nơi cứu trợ ");
    return;
  }
  if (Fund.text == "" || Fund.text == "0") {
    showAlert("Vui lòng thêm vào số tiền dự định kêu gọi cho " + Locations.selectedOptionLabel);
    return;
  }
  if (CharityType.selectedOptionValue == "") {
    showAlert("Vui lòng chọn loại hình quyên góp ");
    return;
  } else {
    CharityType.setDisabled(true);
  }
  var type = {};
  GetSupplyType.data.forEach(x => {
    if (x.id == CharityType.selectedOptionValue) type = x;
  });
  var locations = LocationList.listData;
  locations.forEach(x => {
    if (x.id == Locations.selectedOptionValue) locations.pop(x);
  });
  storeValue("locations", [...locations, {
    id: Locations.selectedOptionValue,
    location: Locations.selectedOptionLabel,
    expected_fund: Fund.value,
    unit: type.unit
  }]);
  closeModal(AddLocation.name);
}

