function () {
  var data = {
    "username": InputUser.text,
    "email": InputEmail.text,
    "password": InputPassWord.text,
    "first_name": InputName.text,
    "last_name": InputLastName.text
  };
  if (Select1.selectedOptionValue === "toChuc") {
    data.civilian_id = InputCCCD.text;
    data.civilian_id_date = DatePickerCCCD.formattedDate;
  }
  fetch(`${appsmith.store.setting.deploy_path}/user/`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(data)
  }).then(response => response.json()).then(data => {
    showAlert("Đăng Ký Thành Công");
    navigateTo("login");
  }).catch(error => {
    console.error('Error:', error);
  });
}

