() => {
  if (InputUser.text == '' || InputPassWord.text == '') {
    TextError.setText("Tên đăng nhập và mật khẩu không được để trống!");
    TextError.setTextColor("#FF0000");
    return;
  } else {
    TextError.setText("");
    LoginAPI.run().then(() => {
      if (LoginAPI.data.access_token) {
        showAlert("Đăng Nhập Thành công!");
        storeValue("token", LoginAPI.data.access_token);
        GetUser.run().then(() => {
          storeValue("user", GetUser.data);
          navigateTo("Index");
        });
      } else {
        TextError.setText("Đăng Nhập Thất bại");
        TextError.setTextColor("#FF0000");
      }
    }).catch(error => {
      TextError.setText("Lỗi kết nối");
      TextError.setTextColor("#FF0000");
      console.error('Lỗi:', error);
    });
  }
}

