actionConfiguration.body:
() => {
  storeValue("coord", {
    latitude: 0,
    longitude: 0
  });
  setInterval(() => {
    var old = appsmith.store.coord;
    var newpos = {
      latitude: 0,
      longitude: 0
    };
    appsmith.geolocation.getCurrentPosition(location => {
      var position = location.coords;
      newpos.latitude = position.latitude - old.latitude > 1 ? position.latitude : old.latitude;
      newpos.longitude = position.longitude - old.longitude > 1 ? position.longitude : old.longitude;
      if (newpos.latitude != old.latitude || newpos.longitude != old.longitude) {
        GetLocation.run(newpos).then(() => {
          storeValue("coord", newpos);
          storeValue("Map", GetLocation.data);
          console.log(GetLocation.data);
        });
      }
    });
  }, 5000);
}

