unpublishedCollection.body:
export default {
	OnLoadPage: ()  => {
		GetSupplyType.run().then(() => {
			var data = [];
			GetSupplyType.data.forEach((x) => {
				data = [...data, {"label": x.type, "value": x.id }]
			})
			CharityType.setOptions(data)
		});

		GetLocation.run().then(() => {
			var data = [];
			GetLocation.data.forEach((x) => {
				data = [...data, {"label": x.location, "value": x.id }]
			})
			Locations.setOptions(data)
		})

		storeValue("locations", [])
		storeValue("pictures", [])
		storeValue("articles", "")
		storeValue("article_id", [])
	},

	AddLocationGoal: () => {
		if(Locations.selectedOptionValue == ''){
			showAlert("Vui lòng thêm vào nơi cứu trợ ")
			return;
		}
		if(Fund.text == "" || Fund.text == "0"){
			showAlert("Vui lòng thêm vào số tiền dự định kêu gọi cho " + Locations.selectedOptionLabel)
			return;
		}
		if(CharityType.selectedOptionValue == ""){
			showAlert("Vui lòng chọn loại hình quyên góp ")
			return;
		}
		else {
			CharityType.setDisabled(true);
		}

		var type = {}
		GetSupplyType.data.forEach((x) => {
			if(x.id == CharityType.selectedOptionValue)
				type = x;
		})
		var locations = LocationList.listData
		locations.forEach((x) => {
			if(x.id == Locations.selectedOptionValue)
				locations.pop(x)
		})
		storeValue("locations", [...locations, {id:Locations.selectedOptionValue, location: Locations.selectedOptionLabel, expected_fund: Fund.value, unit: type.unit}])
		closeModal(AddLocation.name)
	},
	AddCampagn: () => {
		if(Title.text == '' || Content.text == '' || appsmith.store.locations.length == 0){
			showAlert("Vui lòng điền thông tin đầy đủ")
			return;
		}
		if (!confirm.isChecked){
			showAlert("Bạn chưa xác nhận đăng kí")
			return;
		}
		let starDate = new Date(StartDate.selectedDate);
		let endDate = new Date(EndDate.selectedDate);
		// Format the date to 'YYYY-MM-DD'
		let formattedStartDate = starDate.toISOString().split('T')[0];
		let formattedEndDate = endDate.toISOString().split('T')[0];
		PostCampaign.run({StartDate: formattedStartDate, EndDate: formattedEndDate}).then(() => {
			if(typeof PostCampaign.data == "string")
				showAlert(PostCampaign.data)
			else{
				showAlert("Đăng kí thành công, Admin đang phê duyệt hoạt động này..")
				showModal(AddPicture.name)
			}

			console.log(typeof PostCampaign.data)
		})
	},
	AddPicture: () => {
		PostPictureCampaign.run().then(()=>{
			if(typeof PostPictureCampaign.data == "string")
				showAlert(PostPictureCampaign.data)
			else{
