export default {
	Button1onClick: async () => {
		const boundary = "----WebKitFormBoundary" + Math.random().toString(36).substr(2);
		const dataprimary = [];

		// Tạo JSON chính
		await Promise.all(
			List1.currentItemsView.map(async (currentItem) => {
				const data = {};
				data.paid = currentItem.CurrencyInput1.value;
				data.paid_for = currentItem.Input1.text;
				dataprimary.push(data);
			})
		);

		let body = `--${boundary}\r\n`;
		body += `Content-Disposition: form-data; name="details"\r\n\r\n`;
		body += `${JSON.stringify(dataprimary)}\r\n`;

		FilePicker1.files.forEach((file, index) => {
			body += `--${boundary}\r\n`;
			body += `Content-Disposition: form-data; name="file[${index}]"; filename="${file.name}"\r\n`;
			body += `Content-Type: ${file.type || "application/octet-stream"}\r\n\r\n`;
			body += `${file}\r\n`;
		});

		body += `--${boundary}--`;
		let pk = Select1.selectedOptionValue;

		const token = appsmith.store.token;
		console.log(body)
		const response = await fetch(`${appsmith.store.setting.deploy_path}/donation_campaign/${pk}/report/`, {
			method: "POST",
			headers: {
				"Content-Type": `multipart/form-data; boundary=${boundary}`,
				"Authorization": `Bearer ${token}`,
			},
			body: body,
		});

		if (response.ok) {
			const result = await response.json();
			showAlert("Success: " + JSON.stringify(result, null, 2));
			navigateTo("Index")
		} else {
			showAlert("Error: " + response.statusText);
		}

	},
	logout: () => {
		if(appsmith.store.token != null)
			storeValue("token", null)
		if(appsmith.store.user != null)
			storeValue("user", null)
		navigateTo("Login")
	}
};

