export default {
	resources: {
		vn: {
			translation: Constant.defaultLanguageKeys
		}
	},
	getResources() {
		return this.resources;
	},
	async selectLanguage(lang = appsmith.store.lang.value){
		if (!this.resources[lang]){
			await getLanguageValuesAI.run();
			this.resources[lang] = {
				translation: JSON.parse(getLanguageValuesAI.data.response)
			};
			this.setup(lang);
		} else {
			i18next.changeLanguage(lang);
			this.translate();
		}
	},
	async setup(lang=appsmith.store.lang.value){
		await i18next.init({
			debug: true,
			resources: this.resources
		});
		await i18next.changeLanguage(lang);
		this.translate();

	},
	translate(key){
		return i18next.t(key)
	}
}

